import './App.css';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import React from 'react'
import Clarifai from 'clarifai';
import Particles from 'react-particles-js';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
// const Clarifai = require('clarifai')
// import Clarifai from 'C:/Users/ASUS/Desktop/udemy react/facerecog/node_modules/clarifai/dist/index.js'

const app = new Clarifai.App({
  apiKey: 'e8c616aec8a34b73adfd146d2db4b387'
 });
 const particlesOptions = {
  particles: {
    number: {
      value: 30,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
}

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      input : '',
      imageUrl: ''
    }
  }
  onInputChange = (event) =>{
    // console.log(event.target.value)
    this.setState({input: event.target.value}) ;
    // console.log(this.state.input)
  }
  onButtonSubmit = () =>{
    this.setState({imageUrl: this.state.input})
    console.log(this.state.imageUrl)

    app.models.predict(
      Clarifai.COLOR_MODEL,
      "https://samples.clarifai.com/face-det.jpg").then(
        function(response){
          console.log(response)
        },
        function(err){

        }
        );
  }
  
  render() {
    return (
      <div className="App">
        <Particles className='particles'
          params={particlesOptions}
        />

        <Navigation></Navigation>
        <Logo></Logo>
        <Rank></Rank>
        <ImageLinkForm 
        onInputChange = {this.onInputChange}
        onButtonSubmit = {this.onButtonSubmit}>
        </ImageLinkForm>
        <FaceRecognition imageUrl={this.imageUrl} />

      </div>
    );
  }
}

export default App;
